// üîç Placeholder Typing + Blinking Cursor

let text = "üîç  Search For What You Want...";
let input;
let i = 0;
let cursorVisible = true;
let typing;
let interval;

function updatePlaceholder() {
  if (!input) return;
  let displayed = text.substring(0, i);
  if (cursorVisible) {
    input.setAttribute("placeholder", displayed + "|");
  } else {
    input.setAttribute("placeholder", displayed + " ");
  }
}

function typeAndBlink() {
  // Blink cursor while typing
  typing = setInterval(() => {
    cursorVisible = !cursorVisible;
    updatePlaceholder();
  }, 100);

  function typeChar() {
    if (!input) return;
    if (i <= text.length) {
      updatePlaceholder();
      i++;
      setTimeout(typeChar, 100);
    } else {
      clearInterval(typing);
      startBlinkAtEnd();
    }
  }
  typeChar();
}

function startBlinkAtEnd() {
  interval = setInterval(() => {
    cursorVisible = !cursorVisible;
    let displayed = text;
    if (cursorVisible) {
      input.setAttribute("placeholder", displayed + "|");
    } else {
      input.setAttribute("placeholder", displayed + " ");
    }
  }, 500);

  // Restart typing loop after pause
  setTimeout(() => {
    clearInterval(interval);
    i = 0;
    cursorVisible = true;
    typeAndBlink();
  }, 2500);
}

export const typePlaceholder = () => {
  input = document.querySelector("#search");
  if (!input) return;
  // Clear old intervals if any
  clearInterval(typing);
  clearInterval(interval);
  i = 0;
  cursorVisible = true;
  typeAndBlink();
};

// Display Device Battery

navigator.getBattery().then(function (battery) {
  function updateBattery() {
    const level = Math.floor(battery.level * 100);
    const cartNum = document.querySelector(".cart_num");
    document.getElementById("level").style.width = level + "%";
    document.getElementById("info").innerText = `${level}% ${
      battery.charging ? "Charging" : ""
    }`;
    if (battery.charging) {
      cartNum.classList.remove("not-charging");
    } else {
      cartNum.classList.add("not-charging");
    }
  }
  updateBattery();

  // Listen For Changes
  battery.addEventListener("levelchange", updateBattery);
  battery.addEventListener("chargingchange", updateBattery);
});

function getBasePath() {
  let path = window.location.pathname;

  // Normalize multiple slashes
  path = path.replace(/\/{2,}/g, "/");

  // Find "HTML" folder
  let index = path.indexOf("/HTML/");
  if (index !== -1) {
    // Keep everything up to "/HTML/"
    path = path.substring(0, index + 6); // "/HTML/"
  } else {
    // Fallback: current folder
    path = "./";
  }

  // Ensure trailing slash
  if (!path.endsWith("/")) {
    path += "/";
  }

  return path;
}

