export const Shop = () => {
  const productParent = document.querySelector(".product_child2");
  const product = document.querySelector(".product_parent");
  const filter = document.querySelector("#filter");
  const adventure = document.querySelector(".adventure");

  const resetLayout = () => {
    productParent.style.display = "block";
    document.body.classList.add("sidebar-active");
    adventure.style.display = "none";
    product.style.flexDirection = "row";
    product.style.marginLeft = "0";
    filter.style.marginRight = "0";
  };

  // When clicking Shop ‚ñº ‚Üí hide products
  document.querySelector("#shop").addEventListener("click", (e) => {
    e.preventDefault(); // stop default link behavior
    productParent.style.display = "none";
    document.body.classList.remove("sidebar-active");
    adventure.style.display = "block";
    filter.style.marginRight = "73px";
    updateItemsPerPage();
    showPage(1);
    renderPagination();
  });

  // When clicking Right Sidebar ‚Üí show products
  document.querySelector("#right").addEventListener("click", (e) => {
    e.preventDefault(); // stop default link behavior
    resetLayout();
    productParent.style.display = "block";
    document.body.classList.add("sidebar-active");
    adventure.style.display = "none";
    filter.style.marginRight = "55px";
    updateItemsPerPage();
    showPage(1);
    renderPagination();
  });

  // When clicking Left Sidebar ‚Üí show products
  document.querySelector("#left").addEventListener("click", (e) => {
    e.preventDefault(); // stop default link behavior
    resetLayout();
    adventure.style.display = "none";
    document.body.classList.add("sidebar-active");
    productParent.style.display = "block";
    product.style.flexDirection = "row-reverse";
    product.style.marginLeft = "50px";
    filter.style.marginRight = "55px";
    updateItemsPerPage();
    showPage(1);
    renderPagination();
  });
};

const updateCartCount = async () => {
  try {
    let res = await fetch(apiCart);
    let cartItems = await res.json();
    document.querySelector("#cart_count").innerText = cartItems.length;
  } catch (err) {
    console.log(err);
  }
};

document.addEventListener("DOMContentLoaded", function () {
  const cart_Page = document.querySelector(".cart");
  if (cart_Page) {
    cart_Page.addEventListener("click", (e) => {
      window.location.href = "Cart.html";
    });
  }
  updateCartCount();
});
